		    public Form(int id, string title = "")
		    {
		        FormId = id;
		        FormInputs = new List<FormInputBase>();
		        CurrentInputIds = new HashSet<int>();
		        FormTitle = title;
		    }
		    private HashSet<int> CurrentInputIds;
		    public void AddFormInput(FormInputBase input)
